mapper.delete:
DELETE
FROM
`LR_LRC_IP_WHITE`
WHERE
1 = 1

    AND lrc_ip_white_id = #{lrcIpWhiteId}


    AND lrc_ip_white_key = #{lrcIpWhiteKey}


    AND lrc_key = #{lrcKey}


    AND ip_address = #{ipAddress}

mapper.select:
SELECT
lrc_ip_white_id AS lrcIpWhiteId,
lrc_ip_white_key AS lrcIpWhiteKey,
lrc_key AS lrcKey,
ip_address AS ipAddress
FROM
`LR_LRC_IP_WHITE`
WHERE
1 = 1

    AND lrc_ip_white_id = #{lrcIpWhiteId}


    AND lrc_ip_white_key = #{lrcIpWhiteKey}


    AND lrc_key = #{lrcKey}


    AND ip_address = #{ipAddress}

SELECT COUNT(*)
FROM
`LR_LRC_IP_WHITE`
WHERE
1 = 1

    AND lrc_ip_white_id = #{lrcIpWhiteId}


    AND lrc_ip_white_key = #{lrcIpWhiteKey}


    AND lrc_key = #{lrcKey}


    AND ip_address = #{ipAddress}
