mapper.delete:
DELETE
FROM
`LR_LRC_IP_WHITE`
WHERE
1 = 1

    AND lrcIpWhiteId = #{lrcIpWhiteId}


    AND lrcIpWhiteKey = #{lrcIpWhiteKey}


    AND lrcKey = #{lrcKey}


    AND ipAddress = #{ipAddress}

mapper.select:
SELECT
lrcIpWhiteId AS lrcIpWhiteId,
lrcIpWhiteKey AS lrcIpWhiteKey,
lrcKey AS lrcKey,
ipAddress AS ipAddress
FROM
`LR_LRC_IP_WHITE`
WHERE
1 = 1

    AND lrcIpWhiteId = #{lrcIpWhiteId}


    AND lrcIpWhiteKey = #{lrcIpWhiteKey}


    AND lrcKey = #{lrcKey}


    AND ipAddress = #{ipAddress}

SELECT COUNT(*)
FROM
`LR_LRC_IP_WHITE`
WHERE
1 = 1

    AND lrcIpWhiteId = #{lrcIpWhiteId}


    AND lrcIpWhiteKey = #{lrcIpWhiteKey}


    AND lrcKey = #{lrcKey}


    AND ipAddress = #{ipAddress}
