mapper.delete:
DELETE
FROM
`LR_LRC`
WHERE
1 = 1

    AND lrcId = #{lrcId}


    AND lrcKey = #{lrcKey}


    AND createTime = #{createTime}


    AND intro = #{intro}


    AND type = #{type}


    AND publicKey = #{publicKey}


    AND privateKey = #{privateKey}

mapper.select:
SELECT
lrcId AS lrcId,
lrcKey AS lrcKey,
createTime AS createTime,
intro AS intro,
type AS type,
publicKey AS publicKey,
privateKey AS privateKey
FROM
`LR_LRC`
WHERE
1 = 1

    AND lrcId = #{lrcId}


    AND lrcKey = #{lrcKey}


    AND createTime = #{createTime}


    AND intro = #{intro}


    AND type = #{type}


    AND publicKey = #{publicKey}


    AND privateKey = #{privateKey}

SELECT COUNT(*)
FROM
`LR_LRC`
WHERE
1 = 1

    AND lrcId = #{lrcId}


    AND lrcKey = #{lrcKey}


    AND createTime = #{createTime}


    AND intro = #{intro}


    AND type = #{type}


    AND publicKey = #{publicKey}


    AND privateKey = #{privateKey}
