mapper.delete:
DELETE
FROM
`LR_LRC_SESSION`
WHERE
1 = 1

    AND lrcSessionId = #{lrcSessionId}


    AND lrcSessionKey = #{lrcSessionKey}


    AND lrcKey = #{lrcKey}


    AND lrcs = #{lrcs}

mapper.select:
SELECT
lrcSessionId AS lrcSessionId,
lrcSessionKey AS lrcSessionKey,
lrcKey AS lrcKey,
lrcs AS lrcs
FROM
`LR_LRC_SESSION`
WHERE
1 = 1

    AND lrcSessionId = #{lrcSessionId}


    AND lrcSessionKey = #{lrcSessionKey}


    AND lrcKey = #{lrcKey}


    AND lrcs = #{lrcs}

SELECT COUNT(*)
FROM
`LR_LRC_SESSION`
WHERE
1 = 1

    AND lrcSessionId = #{lrcSessionId}


    AND lrcSessionKey = #{lrcSessionKey}


    AND lrcKey = #{lrcKey}


    AND lrcs = #{lrcs}
