mapper.delete:
DELETE
FROM
`LR_LRC_SESSION`
WHERE
1 = 1

    AND lrcSessionId = #{lrcSessionId}


    AND lrcSessionKey = #{lrcSessionKey}


    AND lrcKey = #{lrcKey}


    AND lrcs = #{lrcs}


    AND ipAddress = #{ipAddress}


    AND activeTime = #{activeTime}

mapper.select:
SELECT
lrcSessionId AS lrcSessionId,
lrcSessionKey AS lrcSessionKey,
lrcKey AS lrcKey,
lrcs AS lrcs,
ipAddress AS ipAddress,
activeTime AS activeTime
FROM
`LR_LRC_SESSION`
WHERE
1 = 1

    AND lrcSessionId = #{lrcSessionId}


    AND lrcSessionKey = #{lrcSessionKey}


    AND lrcKey = #{lrcKey}


    AND lrcs = #{lrcs}


    AND ipAddress = #{ipAddress}


    AND activeTime = #{activeTime}

SELECT COUNT(*)
FROM
`LR_LRC_SESSION`
WHERE
1 = 1

    AND lrcSessionId = #{lrcSessionId}


    AND lrcSessionKey = #{lrcSessionKey}


    AND lrcKey = #{lrcKey}


    AND lrcs = #{lrcs}


    AND ipAddress = #{ipAddress}


    AND activeTime = #{activeTime}
