mapper.delete:
DELETE
FROM
`LR_LRC`
WHERE
1 = 1

    AND lrc_id = #{lrcId}


    AND lrc_key = #{lrcKey}


    AND create_time = #{createTime}


    AND intro = #{intro}


    AND type = #{type}


    AND public_key = #{publicKey}


    AND private_key = #{privateKey}

mapper.select:
SELECT
lrc_id AS lrcId,
lrc_key AS lrcKey,
create_time AS createTime,
intro AS intro,
type AS type,
public_key AS publicKey,
private_key AS privateKey
FROM
`LR_LRC`
WHERE
1 = 1

    AND lrc_id = #{lrcId}


    AND lrc_key = #{lrcKey}


    AND create_time = #{createTime}


    AND intro = #{intro}


    AND type = #{type}


    AND public_key = #{publicKey}


    AND private_key = #{privateKey}

SELECT COUNT(*)
FROM
`LR_LRC`
WHERE
1 = 1

    AND lrc_id = #{lrcId}


    AND lrc_key = #{lrcKey}


    AND create_time = #{createTime}


    AND intro = #{intro}


    AND type = #{type}


    AND public_key = #{publicKey}


    AND private_key = #{privateKey}
