mapper.insert:
INSERT INTO
`LR_T_DSD_REL` (
    t_dsd_rel_id,
    task_key,
    data_set_def_key
) VALUES (
    #{tDsdRelId},
    #{taskKey},
    #{dataSetDefKey}
)

mapper.delete:
DELETE
FROM
`LR_T_DSD_REL`
WHERE
1 = 1

    AND t_dsd_rel_id = #{tDsdRelId}


    AND task_key = #{taskKey}


    AND data_set_def_key = #{dataSetDefKey}

mapper.select:
SELECT t_dsd_rel_id AS tDsdRelId, task_key AS taskKey, data_set_def_key AS dataSetDefKey FROM `LR_T_DSD_REL` WHERE 1 = 1
AND t_dsd_rel_id = #{tDsdRelId}
AND task_key = #{taskKey}
AND data_set_def_key = #{dataSetDefKey}

SELECT COUNT(*)
FROM
`LR_T_DSD_REL`
WHERE
1 = 1

    AND t_dsd_rel_id = #{tDsdRelId}


    AND task_key = #{taskKey}


    AND data_set_def_key = #{dataSetDefKey}
