mapper.insert:
INSERT INTO
`LR_T_TIS_REL` (
    t_tis_rel_id,
    task_key,
    task_instruction_set_key
) VALUES (
    #{tTisRelId},
    #{taskKey},
    #{taskInstructionSetKey}
)

mapper.delete:
DELETE
FROM
`LR_T_TIS_REL`
WHERE
1 = 1

    AND t_tis_rel_id = #{tTisRelId}


    AND task_key = #{taskKey}


    AND task_instruction_set_key = #{taskInstructionSetKey}

mapper.select:
SELECT t_tis_rel_id AS tTisRelId, task_key AS taskKey, task_instruction_set_key AS taskInstructionSetKey FROM `LR_T_TIS_REL` WHERE 1 = 1
AND t_tis_rel_id = #{tTisRelId}
AND task_key = #{taskKey}
AND task_instruction_set_key = #{taskInstructionSetKey}

SELECT COUNT(*)
FROM
`LR_T_TIS_REL`
WHERE
1 = 1

    AND t_tis_rel_id = #{tTisRelId}


    AND task_key = #{taskKey}


    AND task_instruction_set_key = #{taskInstructionSetKey}
