mapper.insert:
INSERT INTO
`LR_T_TPD_REL` (
    t_tpd_rel_id,
    task_key,
    task_parameter_def_key
) VALUES (
    #{tTpdRelId},
    #{taskKey},
    #{taskParameterDefKey}
)

mapper.delete:
DELETE
FROM
`LR_T_TPD_REL`
WHERE
1 = 1

    AND t_tpd_rel_id = #{tTpdRelId}


    AND task_key = #{taskKey}


    AND task_parameter_def_key = #{taskParameterDefKey}

mapper.select:
SELECT t_tpd_rel_id AS tTpdRelId, task_key AS taskKey, task_parameter_def_key AS taskParameterDefKey FROM `LR_T_TPD_REL` WHERE 1 = 1
AND t_tpd_rel_id = #{tTpdRelId}
AND task_key = #{taskKey}
AND task_parameter_def_key = #{taskParameterDefKey}

SELECT COUNT(*)
FROM
`LR_T_TPD_REL`
WHERE
1 = 1

    AND t_tpd_rel_id = #{tTpdRelId}


    AND task_key = #{taskKey}


    AND task_parameter_def_key = #{taskParameterDefKey}
