mapper.delete:
DELETE
FROM
`LR_TASK`
WHERE
1 = 1

    AND task_id = #{taskId}


    AND task_key = #{taskKey}


    AND task_name = #{taskName}


    AND create_time = #{createTime}

mapper.select:
SELECT
task_id AS taskId,
task_key AS taskKey,
task_name AS taskName,
create_time AS createTime
FROM
`LR_TASK`
WHERE
1 = 1

    AND task_id = #{taskId}


    AND task_key = #{taskKey}


    AND task_name = #{taskName}


    AND create_time = #{createTime}

SELECT COUNT(*)
FROM
`LR_TASK`
WHERE
1 = 1

    AND task_id = #{taskId}


    AND task_key = #{taskKey}


    AND task_name = #{taskName}


    AND create_time = #{createTime}
